import React from 'react';
import './Skills.css';

const Skills = () => {
  const skillCategories = [
    {
      title: "Programming Languages",
      skills: [
        { name: "C", level: 90 },
        { name: "Python", level: 95 },
        { name: "Java", level: 85 },
        { name: "MATLAB", level: 80 },
        { name: "JavaScript", level: 85 }
      ]
    },
    {
      title: "Embedded Systems",
      skills: [
        { name: "Arduino", level: 95 },
        { name: "Raspberry Pi", level: 90 },
        { name: "LPC2148", level: 85 },
        { name: "IoT (NodeMCU, ESP8266)", level: 90 },
        { name: "RTOS", level: 80 }
      ]
    },
    {
      title: "AI/ML & Computer Vision",
      skills: [
        { name: "Computer Vision (OpenCV)", level: 90 },
        { name: "Scikit-learn", level: 85 },
        { name: "Python AI Libraries", level: 88 },
        { name: "Machine Learning", level: 85 },
        { name: "Data Analysis", level: 82 }
      ]
    },
    {
      title: "Software Engineering",
      skills: [
        { name: "Data Structures & Algorithms", level: 88 },
        { name: "Git", level: 90 },
        { name: "OS Concepts", level: 85 },
        { name: "System Design", level: 80 },
        { name: "Problem Solving", level: 92 }
      ]
    },
    {
      title: "Web Development",
      skills: [
        { name: "HTML", level: 95 },
        { name: "CSS", level: 90 },
        { name: "JavaScript", level: 85 },
        { name: "React", level: 88 },
        { name: "Responsive Design", level: 90 }
      ]
    }
  ];

  const certifications = [
    "IoT Internship Certificate - Acmegrade (4 months)",
    "Embedded Systems Internship Certificate - Indo German Tool Room (2 months)",
    "Schneider Electric India Foundation Training"
  ];

  return (
    <section id="skills" className="skills">
      <div className="container">
        <h2 className="section-title">Technical Skills</h2>
        <p className="section-subtitle">
          Comprehensive expertise across embedded systems, AI/ML, and software development
        </p>

        <div className="skills-grid">
          {skillCategories.map((category, categoryIndex) => (
            <div key={categoryIndex} className="skill-category">
              <h3 className="category-title">{category.title}</h3>
              <div className="skills-list">
                {category.skills.map((skill, skillIndex) => (
                  <div key={skillIndex} className="skill-item">
                    <div className="skill-header">
                      <span className="skill-name">{skill.name}</span>
                      <span className="skill-percentage">{skill.level}%</span>
                    </div>
                    <div className="skill-bar">
                      <div 
                        className="skill-progress" 
                        style={{ width: `${skill.level}%` }}
                      ></div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>

        <div className="certifications-section">
          <h3 className="certifications-title">Certifications</h3>
          <div className="certifications-list">
            {certifications.map((cert, index) => (
              <div key={index} className="certification-item">
                <div className="cert-icon">üèÜ</div>
                <span className="cert-text">{cert}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Skills;
