import React, { useEffect, useRef, useState } from 'react';

const CustomCursor = () => {
  const cursorRef = useRef(null);
  const cursorPos = useRef({ x: 0, y: 0 });
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    const cursor = cursorRef.current;
    if (!cursor) return;

    let rafId;
    let isHovering = false;
    let isClicking = false;

    const updateCursor = () => {
      cursor.style.left = `${cursorPos.current.x - 6}px`;
      cursor.style.top = `${cursorPos.current.y - 6}px`;
    };

    const handleMouseMove = (e) => {
      cursorPos.current = { x: e.clientX, y: e.clientY };

      // Show cursor when moving
      if (!isVisible) setIsVisible(true);

      cancelAnimationFrame(rafId);
      rafId = requestAnimationFrame(updateCursor);
    };

    const handleMouseEnter = (e) => {
      if (!isHovering) {
        // Check if it's a text input element
        if (e.target.matches('input[type="text"], input[type="email"], textarea')) {
          cursor.classList.add('text');
        } else {
          cursor.classList.add('hover');
        }
        isHovering = true;
      }
    };

    const handleMouseLeave = () => {
      if (isHovering) {
        cursor.classList.remove('hover', 'text');
        isHovering = false;
      }
    };

    const handleMouseDown = () => {
      if (!isClicking) {
        cursor.classList.add('click');
        isClicking = true;
      }
    };

    const handleMouseUp = () => {
      if (isClicking) {
        cursor.classList.remove('click');
        isClicking = false;
      }
    };

    const handleMouseLeaveWindow = () => {
      setIsVisible(false);
    };

    const handleMouseEnterWindow = () => {
      setIsVisible(true);
    };

    // Add event listeners
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mouseup', handleMouseUp);
    document.addEventListener('mouseleave', handleMouseLeaveWindow);
    document.addEventListener('mouseenter', handleMouseEnterWindow);

    // Add hover effects for interactive elements
    const interactiveElements = document.querySelectorAll('a, button, .btn, .project-card, .tech-card, .contact-card, .certification-card, .achievement-card, .stat-item, .social-link, .nav-link, .theme-toggle, input, textarea, select');

    interactiveElements.forEach(el => {
      el.addEventListener('mouseenter', handleMouseEnter);
      el.addEventListener('mouseleave', handleMouseLeave);
    });

    // Re-query elements periodically for dynamically added content
    const intervalId = setInterval(() => {
      const newElements = document.querySelectorAll('a, button, .btn, .project-card, .tech-card, .contact-card, .certification-card, .achievement-card, .stat-item, .social-link, .nav-link, .theme-toggle, input, textarea, select');
      newElements.forEach(el => {
        el.addEventListener('mouseenter', handleMouseEnter);
        el.addEventListener('mouseleave', handleMouseLeave);
      });
    }, 2000);

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mouseup', handleMouseUp);
      document.removeEventListener('mouseleave', handleMouseLeaveWindow);
      document.removeEventListener('mouseenter', handleMouseEnterWindow);
      cancelAnimationFrame(rafId);
      clearInterval(intervalId);

      interactiveElements.forEach(el => {
        el.removeEventListener('mouseenter', handleMouseEnter);
        el.removeEventListener('mouseleave', handleMouseLeave);
      });
    };
  }, [isVisible]);

  return (
    <div 
      ref={cursorRef} 
      className="custom-cursor" 
      style={{ opacity: isVisible ? 1 : 0 }}
    />
  );
};

export default CustomCursor;
