import React, { useEffect, useRef } from 'react';

const CustomCursor = () => {
  const cursorRef = useRef(null);

  useEffect(() => {
    const cursor = cursorRef.current;
    if (!cursor) return;

    const handleMouseMove = (e) => {
      const x = e.clientX;
      const y = e.clientY;

      cursor.style.left = `${x - 6}px`;
      cursor.style.top = `${y - 6}px`;
    };

    const handleMouseEnter = (e) => {
      const target = e.target;

      // Check if it's a text input element
      if (target.matches('input[type="text"], input[type="email"], textarea, input[name="name"], input[name="email"]')) {
        cursor.classList.add('text');
        cursor.classList.remove('hover');
      } else if (target.matches('a, button, .btn, .project-card, .tech-card, .contact-card, .certification-card, .achievement-card, .stat-item, .social-link, .nav-link, .theme-toggle, select')) {
        cursor.classList.add('hover');
        cursor.classList.remove('text');
      }
    };

    const handleMouseLeave = (e) => {
      const target = e.target;

      if (target.matches('input, textarea, select, a, button, .btn, .project-card, .tech-card, .contact-card, .certification-card, .achievement-card, .stat-item, .social-link, .nav-link, .theme-toggle')) {
        cursor.classList.remove('hover', 'text');
      }
    };

    const handleMouseDown = () => {
      cursor.classList.add('click');
    };

    const handleMouseUp = () => {
      cursor.classList.remove('click');
    };

    // Add global event listeners
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mousedown', handleMouseDown);
    document.addEventListener('mouseup', handleMouseUp);

    // Add event listeners to all interactive elements
    const addListenersToElements = () => {
      const elements = document.querySelectorAll('a, button, .btn, .project-card, .tech-card, .contact-card, .certification-card, .achievement-card, .stat-item, .social-link, .nav-link, .theme-toggle, input, textarea, select');

      elements.forEach(el => {
        el.addEventListener('mouseenter', handleMouseEnter);
        el.addEventListener('mouseleave', handleMouseLeave);
      });
    };

    // Initial setup
    addListenersToElements();

    // Clean up
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mousedown', handleMouseDown);
      document.removeEventListener('mouseup', handleMouseUp);

      // Remove listeners from elements
      const elements = document.querySelectorAll('a, button, .btn, .project-card, .tech-card, .contact-card, .certification-card, .achievement-card, .stat-item, .social-link, .nav-link, .theme-toggle, input, textarea, select');
      elements.forEach(el => {
        el.removeEventListener('mouseenter', handleMouseEnter);
        el.removeEventListener('mouseleave', handleMouseLeave);
      });
    };
  }, []);

  return <div ref={cursorRef} className="custom-cursor" />;
};

export default CustomCursor;
